import { Message } from "ai";

type OutgoingMessage =
  | {
      type: "cf_agent_chat_messages";
      messages: Message[];
    }
  | {
      type: "cf_agent_use_chat_response";
      id: string;
      body: string;
      done: boolean;
    }
  | {
      type: "cf_agent_chat_messages";
      messages: Message[];
    }
  | {
      type: "cf_agent_chat_clear";
    };
type IncomingMessage =
  | {
      type: "cf_agent_chat_init";
      messages: Message[];
    }
  | {
      type: "cf_agent_use_chat_request";
      id: string;
      init: Pick<
        RequestInit,
        | "method"
        | "keepalive"
        | "headers"
        | "body"
        | "redirect"
        | "integrity"
        | "credentials"
        | "mode"
        | "referrer"
        | "referrerPolicy"
        | "window"
      >;
    }
  | {
      type: "cf_agent_chat_clear";
    }
  | {
      type: "cf_agent_chat_messages";
      messages: Message[];
    };

export type { IncomingMessage, OutgoingMessage };
